    /**
     *
     * @param c
     * @return
     */
    public int getIndex(String c) {
        int index = 0;
        for (int i = 0; i < prudcts.length; i++) {
            if (prudcts[i].equals(c)) {
                index = i;
                break;
            }
        }
        return index;
    }

